import logo from './logo.svg';
import './App.css';
import request from 'superagent';

// https://reactjs.org/docs/lists-and-keys.html


function ListView(props) {
console.log(props.data.Towns)

const data = props.data.Towns;

const towns = data.map((town) =>
  <div key={town.Name} >
    <li> {town.Name} </li>
  </div>
)

console.log(towns)

return <ul> {towns} </ul>
}

class App extends Component {

  constructor(props){
    super();
    this.state = ({
      notes: []
    })
  }

  componentDidMount() {
    request
      .get("https://api.jsonbin.io/b/5c0a79291deea01014bf1d32/latest")
      .set('secret-key', '$2a$10$2grg4TqBV8/1xtgERZ9Td.1qxuq4FuOXywCg073pAlFOtfNvMmqQS')
      .then( (res) => {
          this.setState({
            notes: res.body,
            loading: false
          });
        console.log(this.state.notes);
      });
  }


